import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../user-service.service';
import { User } from '../user';



@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})
export class UserFormComponent {

  user: User;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private userService: UserService) {
    this.user = new User();
  }

  onSubmit() {
    this.userService.save(this.user).subscribe(result => this.gotoUserList());
  }

  gotoUserList() {
    this.router.navigate(['/users']);
  }

}
<div class="card my-5">
    <div class="card-body">
        <form (ngSubmit)="onSubmit()" #userForm="ngForm">
            <div class="form-group">
                <label for="firstName">First name</label>
                <input type="text" [(ngModel)]="user.firstName" class="form-control" id="firstName" name="firstName"
                    placeholder="Enter your first name" required #name="ngModel">
            </div>
          <div class="form-group">
            <label for="lastName">Last name</label>
            <input type="text" [(ngModel)]="user.lastName" class="form-control" id="lastName" name="lastName"
                   placeholder="Enter your last name" required #name="ngModel">
          </div>
            <div class="form-group">
                <label for="email">Email</label>
                <input type="text" [(ngModel)]="user.email" class="form-control" id="email" name="email"
                    placeholder="Enter your email address" required #email="ngModel">
            </div>
          <div class="form-group">
            <label for="type">Type</label>
            <input type="text" [(ngModel)]="user.type" class="form-control" id="type" name="type"
                   placeholder="Enter the type of user" required #email="ngModel">
          </div>
            <button type="submit" [disabled]="!userForm.form.valid" class="btn btn-dark">Submit</button>
        </form>
    </div>
</div>
