import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TypeService } from '../type-service.service';
import { Type } from '../type';

@Component({
  selector: 'app-type-form',
  templateUrl: './type-form.component.html',
  styleUrls: ['./type-form.component.css']
})
export class TypeFormComponent {

  type: Type;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private typeService: TypeService) {
    this.type = new Type();
  }

  onSubmit() {
    this.typeService.save(this.type).subscribe(result => this.gotoTypeList());
  }

  gotoTypeList() {
    this.router.navigate(['/types']);
  }



<div class="card my-5">
  <div class="card-body">
    <form (ngSubmit)="onSubmit()" #userForm="ngForm">
      <div class="form-group">
        <label for="type">Type name</label>
        <input type="text" [(ngModel)]="type.name" class="form-control" id="type" name="type"
               placeholder="Enter the name of the type" required #name="ngModel">
      </div>
      <button type="submit" [disabled]="!userForm.form.valid" class="btn btn-dark">Submit</button>
    </form>
  </div>
</div>
