import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../user-service.service';
import { User } from '../user';

@Component({
  selector: 'app-user-edit',
  templateUrl: './user-edit.component.html',
  styleUrls: ['./user-edit.component.css']
})
export class UserEditComponent {

  user: User;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private userService: UserService) {
    this.getUser(this.route.snapshot.params['id']);
  }

  onSubmit() {
    this.userService.update(this.user.id, this.user).subscribe(result => this.gotoUserList());
  }

  getUser(id: string){
    this.userService.find(id).subscribe( data =>{
    this.user = data;
    })
  }

  gotoUserList() {
    this.router.navigate(['/users']);
  }


}
<div class="card my-5">
  <div class="card-body">
    <form (ngSubmit)="onSubmit()" #userForm="ngForm">
      <div class="form-group">
        <label for="firstName">First name</label>
        <input type="text" [(ngModel)]="user.firstName" class="form-control" id="firstName" name="firstName"
               placeholder="Enter your first name" required #name="ngModel">{{user.firstName}}
      </div>
      <div class="form-group">
        <label for="lastName">Last name</label>
        <input type="text" [(ngModel)]="user.lastName" class="form-control" id="lastName" name="lastName"
               placeholder="Enter your last name" required #name="ngModel">{{user.lastName}}
      </div>
      <div class="form-group">
        <label for="email">Email</label>
        <input type="text" [(ngModel)]="user.email" class="form-control" id="email" name="email"
               placeholder="Enter your email address" required #email="ngModel">{{user.email}}
      </div>
      <!--          <div class="form-group">
                  <label for="type">Type</label>
                  <input type="text" [(ngModel)]="user.type_id" class="form-control" id="type" name="type"
                         placeholder="Enter the type of user" required #name="ngModel">
                </div>-->
      <button type="submit" [disabled]="!userForm.form.valid" class="btn btn-dark">Submit</button>
    </form>
  </div>
</div>
